      {/*  onDeleteTask={() => deleteTask(tasks[1].id)}*/}
      {/*/>*/}
      {/*<Task*/}
      {/*  text={tasks[2].text}*/}
      {/*  onDeleteTask={() => deleteTask(tasks[2].id)}*/}
      {/*/>*/}
      {/*<Task*/}
      {/*  text={tasks[3].text}*/}
      {/*  onDeleteTask={() => deleteTask(tasks[3].id)}*/}
      {/*/>*/}
    </div>
  );

}















