import './App.css';
import AddTaskForm from "./AddTaskForm/AddTaskForm";
import Task from "./Task/Task";

const App = () => {
  const [tasks, setTasks] = useState([
    {text: 'Add a new task', id: '1'},
    {text: 'Buy milk', id: '2'},
    {text: 'Walk with dog', id: '3'},
    {text: 'Do homework', id: '4'},
  ]);

  const writeTask = (event: React.ChangeEvent<HTMLInputElement>, index: number) => {
    const tasksCopy = [...tasks];
    const taskCopy = {...tasksCopy[index]};
    taskCopy.text = event.target.value;
    tasksCopy[index] = taskCopy;
    setTasks(tasksCopy);
  };

