import React, {useState} from 'react';
import './App.css';
import AddTaskForm from "./AddTaskForm/AddTaskForm";
import Task from "./Task/Task";

const App = () => {
  const [tasks, setTasks] = useState([
    {text: 'Add a new task'},
    {text: 'Buy milk'},
    {text: 'Walk with dog'},
    {text: 'Do homework'},
  ]);

  const addTask = (event: React.ChangeEvent<HTMLInputElement>, index: number) => {
    const tasksCopy = [...tasks];
    const taskCopy = {...tasksCopy[index]};
    taskCopy.text = event.target.value;
    tasksCopy[0] = taskCopy;
    setTasks(tasksCopy);
  };









  return (
    <div className="App">
      <AddTaskForm
        text={tasks[0].text}
        onAddTask={addTask}
      />
      <Task
        text={tasks[1].text}
      />
      <Task text={tasks[2].text} />
      <Task text={tasks[3].text} />
    </div>
  );

}















export default App;
