import React, {useState} from 'react';
import './App.css';
import AddTaskForm from "./AddTaskForm/AddTaskForm";
import Task from "./Task/Task";
import task from "./Task/Task";

const App = () => {
  const [tasks, setTasks] = useState([
    {text: 'Buy milk', id: (Math.floor(Math.random() * (200 - 100 + 1)) + 100).toString(16)},
    {text: 'Walk with dog', id: (Math.floor(Math.random() * (200 - 100 + 1)) + 100).toString(16)},
    {text: 'Do homework', id: (Math.floor(Math.random() * (200 - 100 + 1)) + 100).toString(16)},
  ]);

  // const writeTask = (event: React.ChangeEvent<HTMLInputElement>, index: number) => {
  //   const tasksCopy = [...tasks];
  //   const taskCopy = {...tasksCopy[index]};
  //   taskCopy.text = event.target.value;
  //   tasksCopy[index] = taskCopy;
  //   setTasks(tasksCopy);
  // };

  const [ userInput, setUserInput ] = useState('');

  const writeTask = (event: React.ChangeEvent<HTMLInputElement>) => {
    setUserInput(event.currentTarget.value)
  };

  const handleSubmit = ( event: React.ChangeEvent<HTMLInputElement>) => {
    event.preventDefault();
    addToDo(userInput);
    setUserInput("");
  }

  const addToDo = (userInput: any) => {
    let copy = [...tasks];
    copy = [...copy, { text: userInput, id: (Math.floor(Math.random() * (200 - 100 + 1)) + 100).toString(16) }];
    copy.push({text: userInput, id: (Math.floor(Math.random() * (200 - 100 + 1)) + 100).toString(16) });
    setTasks(copy);
  }

  const deleteTask = (id: string) => {
    const tasksCopy = [...tasks];
    const index = tasks.findIndex(task => task.id === id);
    tasksCopy.splice(index, 1);
    setTasks(tasksCopy);
  };


  const taskList = tasks.map((task, index) => {
      return (
        <Task
          key={task.id}
          text={task.text}
          onDeleteTask={() => deleteTask(task.id)}
        />
      );
    });


  return (
    <div className="App">
      <AddTaskForm
        onWriteTask={writeTask}
        onAddToDo={() => addToDo}
      />
      {taskList}
    </div>
    );
};



export default App;
